#!/bin/sh
basedir=$(dirname "$(echo "$0" | sed -e 's,\\,/,g')")

case `uname` in
    *CYGWIN*|*MINGW*|*MSYS*) basedir=`cygpath -w "$basedir"`;;
esac

if [ -x "$basedir//bin/sh" ]; then
  "$basedir//bin/sh"  "$basedir/../ffmpeg-installer/bin/ffmpeg-installer.sh" "$@"
  ret=$?
else 
  /bin/sh  "$basedir/../ffmpeg-installer/bin/ffmpeg-installer.sh" "$@"
  ret=$?
fi
exit $ret
